import React, { useState, useEffect, useRef } from "react";
import { Bar } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

/*
  Single-file React app for a Paging Simulator.
  - Paste this into src/App.jsx of a Create React App project (or similar)
  - No external libs required.

  Features implemented:
  - Input frames and reference string
  - Algorithms: FIFO, LRU, Optimal
  - Step-by-step simulation, Run/Auto/Reset controls
  - Comparison mode (runs all algorithms and shows faults side-by-side)
  - Simple bar-chart (pure CSS) for comparison
  - Save/Load scenarios using localStorage, Export results as JSON
*/

const parseRefs = (s) => {
  return s
    .split(/[,\s]+/) // commas or spaces
    .map((x) => x.trim())
    .filter((x) => x !== "")
    .map((x) => Number(x));
};

// --- Algorithm implementations (return {faults, snapshots}) ---
function simulateFIFO(refs, framesCount) {
  const frames = Array(framesCount).fill(null);
  const queue = []; // holds frame indices in load order
  const snapshots = [];
  let faults = 0;

  refs.forEach((page, step) => {
    const hitIndex = frames.indexOf(page);
    if (hitIndex !== -1) {
      snapshots.push({ step, page, frames: [...frames], fault: false, evicted: null });
      return;
    }
    faults++;
    const emptyIndex = frames.indexOf(null);
    if (emptyIndex !== -1) {
      frames[emptyIndex] = page;
      queue.push(emptyIndex);
      snapshots.push({ step, page, frames: [...frames], fault: true, evicted: null });
    } else {
      const victim = queue.shift();
      const evicted = frames[victim];
      frames[victim] = page;
      queue.push(victim);
      snapshots.push({ step, page, frames: [...frames], fault: true, evicted });
    }
  });
  return { faults, snapshots };
}

function simulateLRU(refs, framesCount) {
  const frames = Array(framesCount).fill(null);
  // last used time map: page -> last index
  const lastUsed = new Map();
  const snapshots = [];
  let faults = 0;

  refs.forEach((page, step) => {
    const hitIndex = frames.indexOf(page);
    if (hitIndex !== -1) {
      lastUsed.set(page, step);
      snapshots.push({ step, page, frames: [...frames], fault: false, evicted: null });
      return;
    }
    faults++;
    const emptyIndex = frames.indexOf(null);
    if (emptyIndex !== -1) {
      frames[emptyIndex] = page;
      lastUsed.set(page, step);
      snapshots.push({ step, page, frames: [...frames], fault: true, evicted: null });
    } else {
      // choose page with smallest lastUsed
      let victimIndex = 0;
      let victimPage = frames[0];
      let minTime = lastUsed.get(victimPage) ?? -1;
      for (let i = 0; i < frames.length; i++) {
        const p = frames[i];
        const t = lastUsed.get(p) ?? -1;
        if (t < minTime) {
          minTime = t;
          victimIndex = i;
          victimPage = p;
        }
      }
      const evicted = frames[victimIndex];
      // remove evicted page from map
      lastUsed.delete(evicted);
      frames[victimIndex] = page;
      lastUsed.set(page, step);
      snapshots.push({ step, page, frames: [...frames], fault: true, evicted });
    }
  });
  return { faults, snapshots };
}

function simulateOptimal(refs, framesCount) {
  const frames = Array(framesCount).fill(null);
  const snapshots = [];
  let faults = 0;

  refs.forEach((page, step) => {
    const hitIndex = frames.indexOf(page);
    if (hitIndex !== -1) {
      snapshots.push({ step, page, frames: [...frames], fault: false, evicted: null });
      return;
    }
    faults++;
    const emptyIndex = frames.indexOf(null);
    if (emptyIndex !== -1) {
      frames[emptyIndex] = page;
      snapshots.push({ step, page, frames: [...frames], fault: true, evicted: null });
    } else {
      // for each frame, find next use index
      const nextUse = frames.map((p) => {
        const idx = refs.slice(step + 1).indexOf(p);
        return idx === -1 ? Infinity : idx;
      });
      // choose page with largest nextUse
      let victimIndex = 0;
      let maxNext = -1;
      for (let i = 0; i < nextUse.length; i++) {
        const nu = nextUse[i] === Infinity ? Infinity : nextUse[i];
        if (nu === Infinity) {
          victimIndex = i;
          break;
        }
        if (nu > maxNext) {
          maxNext = nu;
          victimIndex = i;
        }
      }
      const evicted = frames[victimIndex];
      frames[victimIndex] = page;
      snapshots.push({ step, page, frames: [...frames], fault: true, evicted });
    }
  });
  return { faults, snapshots };
}

// --- Small utility for deep copying snapshots ---
function copySnapshots(snapshots) {
  return snapshots.map((s) => ({ ...s, frames: [...s.frames] }));
}

// --- Main App ---
export default function App() {
  const [framesCount, setFramesCount] = useState(3);
  const [refString, setRefString] = useState("7,0,1,2,0,3,0,4,2,3,0,3");
  const [algorithm, setAlgorithm] = useState("FIFO");
  const [snapshots, setSnapshots] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);
  const [faults, setFaults] = useState(0);
  const [running, setRunning] = useState(false);
  const [autoInterval, setAutoInterval] = useState(700);
  const timerRef = useRef(null);
  const [lastInputHash, setLastInputHash] = useState(0);

  // comparison results
  const [compareResults, setCompareResults] = useState(null);

  // helper: run selected algorithm and set snapshots
  const runOnce = (alg, refs, fcount) => {
    if (alg === "FIFO") return simulateFIFO(refs, fcount);
    if (alg === "LRU") return simulateLRU(refs, fcount);
    if (alg === "Optimal") return simulateOptimal(refs, fcount);
    return simulateFIFO(refs, fcount);
  };

  const prepareSimulation = () => {
    const refs = parseRefs(refString);
    if (refs.length === 0 || isNaN(framesCount) || framesCount <= 0) {
      alert("Invalid input. Please enter a valid reference string and frames (>0).");
      return false;
    }
    const result = runOnce(algorithm, refs, Number(framesCount));
    setSnapshots(copySnapshots(result.snapshots));
    setFaults(result.faults);
    setCurrentStep(0);
    setCompareResults(null);
    // store a small hash to detect changes
    setLastInputHash(Date.now());
    return true;
  };

  const stepForward = () => {
    setCurrentStep((s) => Math.min(s + 1, snapshots.length));
  };
  const stepBackward = () => {
    setCurrentStep((s) => Math.max(s - 1, 0));
  };

  const runAuto = () => {
    if (running) return;
    setRunning(true);
    timerRef.current = setInterval(() => {
      setCurrentStep((s) => {
        if (s >= snapshots.length) {
          clearInterval(timerRef.current);
          setRunning(false);
          return s;
        }
        return s + 1;
      });
    }, autoInterval);
  };

  useEffect(() => {
    if (currentStep >= snapshots.length) {
      // done
      setRunning(false);
      if (timerRef.current) clearInterval(timerRef.current);
    }
    return () => { };
  }, [currentStep, snapshots.length]);

  const stopAuto = () => {
    setRunning(false);
    if (timerRef.current) clearInterval(timerRef.current);
  };

  const resetSim = () => {
    stopAuto();
    setSnapshots([]);
    setCurrentStep(0);
    setFaults(0);
    setCompareResults(null);
  };

  const runComparison = () => {
    const refs = parseRefs(refString);
    if (refs.length === 0 || isNaN(framesCount) || framesCount <= 0) {
      alert("Invalid input. Please enter a valid reference string and frames (>0).");
      return;
    }
    const fifo = simulateFIFO(refs, Number(framesCount));
    const lru = simulateLRU(refs, Number(framesCount));
    const optimal = simulateOptimal(refs, Number(framesCount));
    setCompareResults({ FIFO: fifo.faults, LRU: lru.faults, Optimal: optimal.faults });
    // also set snapshots to selected algorithm's snapshots for closer look
    const chosen = algorithm === "FIFO" ? fifo : algorithm === "LRU" ? lru : optimal;
    setSnapshots(copySnapshots(chosen.snapshots));
    setFaults(chosen.faults);
    setCurrentStep(0);
  };

  // Save scenario to localStorage
  const saveScenario = () => {
    const key = `paging_scenario_${Date.now()}`;
    const data = { framesCount, refString, algorithm, savedAt: new Date().toISOString() };
    localStorage.setItem(key, JSON.stringify(data));
    alert("Saved scenario to localStorage (key: " + key + ")");
  };
  const loadScenarios = () => {
    const items = Object.keys(localStorage)
      .filter((k) => k.startsWith("paging_scenario_"))
      .map((k) => ({ key: k, data: JSON.parse(localStorage.getItem(k)) }))
      .sort((a, b) => (a.data.savedAt < b.data.savedAt ? 1 : -1));
    return items;
  };

  const exportResults = () => {
    const payload = { framesCount, refString, algorithm, faults, snapshots };
    const blob = new Blob([JSON.stringify(payload, null, 2)], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "paging_simulation_result.json";
    a.click();
    URL.revokeObjectURL(url);
  };

  // derive display snapshot for currentStep (0 = before any step)
  const displaySnapshots = snapshots.slice(0, currentStep);
  const currentSnapshot = snapshots[currentStep - 1] || null;

  return (
    <div style={styles.app}>
      <h1 style={styles.title}>Paging Simulator (React - Frontend only)</h1>

      <div style={styles.panelRow}>
        <div style={styles.panel}>
          <h3>Input</h3>
          <label>Frames: </label>
          <input
            type="number"
            min={1}
            value={framesCount}
            onChange={(e) => setFramesCount(Number(e.target.value))}
            style={styles.input}
          />
          <label>Reference string (comma or space separated):</label>
          <textarea
            value={refString}
            onChange={(e) => setRefString(e.target.value)}
            rows={3}
            style={styles.textarea}
          />
          <label>Algorithm:</label>
          <select value={algorithm} onChange={(e) => setAlgorithm(e.target.value)} style={styles.input}>
            <option>FIFO</option>
            <option>LRU</option>
            <option>Optimal</option>
          </select>

          <div style={{ marginTop: 10 }}>
            <button onClick={prepareSimulation} style={styles.btn}>Prepare</button>
            <button onClick={runComparison} style={styles.btn}>Compare (FIFO/LRU/Optimal)</button>
            <button onClick={saveScenario} style={styles.btn}>Save Scenario</button>
            <button onClick={exportResults} style={styles.btn}>Export JSON</button>
          </div>

          <div style={{ marginTop: 12 }}>
            <small>Saved scenarios:</small>
            <div style={{ maxHeight: 120, overflow: "auto", marginTop: 6 }}>
              {loadScenarios().map((s) => (
                <div key={s.key} style={styles.savedRow}>
                  <div style={{ flex: 1 }}>
                    <strong>{s.key}</strong>
                    <div style={{ fontSize: 12 }}>{s.data.refString} (frames: {s.data.framesCount})</div>
                  </div>
                  <div>
                    <button
                      onClick={() => {
                        setFramesCount(Number(s.data.framesCount));
                        setRefString(s.data.refString);
                        setAlgorithm(s.data.algorithm);
                      }}
                      style={styles.smallBtn}
                    >
                      Load
                    </button>
                    <button
                      onClick={() => {
                        localStorage.removeItem(s.key);
                        // force re-render
                        setLastInputHash(Date.now());
                      }}
                      style={styles.smallBtn}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div style={styles.panelWide}>
          <h3>Simulation</h3>
          <div style={{ marginBottom: 8 }}>
            <button onClick={() => { if (snapshots.length === 0) { if (!prepareSimulation()) return; } stepBackward(); }} style={styles.btn}> Prev</button>
            <button onClick={() => { if (snapshots.length === 0) { if (!prepareSimulation()) return; } stepForward(); }} style={styles.btn}>Next </button>
            <button onClick={() => { if (snapshots.length === 0) { if (!prepareSimulation()) return; } runAuto(); }} style={styles.btn} disabled={running}>Run </button>
            <button onClick={stopAuto} style={styles.btn}>Stop</button>
            <button onClick={resetSim} style={styles.btn}>Reset</button>

            <label style={{ marginLeft: 12 }}>Auto interval (ms): </label>
            <input type="number" value={autoInterval} onChange={(e) => setAutoInterval(Number(e.target.value))} style={{ width: 90 }} />
          </div>

          <div style={{ display: "flex", gap: 12 }}>
            <div style={{ flex: 1 }}>
              <div style={styles.infoRow}>
                <div>Algorithm: <strong>{algorithm}</strong></div>
                <div>Frames: <strong>{framesCount}</strong></div>
                <div>Total refs: <strong>{parseRefs(refString).length}</strong></div>
                <div>Page Faults: <strong>{faults}</strong></div>
                <div>Current Step: <strong>{currentStep} / {snapshots.length}</strong></div>
              </div>

              <div style={{ marginTop: 8 }}>
                <SimulationTable snapshots={displaySnapshots} framesCount={framesCount} currentStep={currentStep} />
              </div>
            </div>

            <div style={{ width: 320 }}>
              <h4>Comparison</h4>
              {compareResults ? (
                <SimpleBarChart data={compareResults} />
              ) : (
                <div style={{ fontSize: 13 }}>Run <strong>Compare</strong> to see faults of FIFO, LRU, and Optimal side-by-side.</div>
              )}

              <div style={{ marginTop: 12 }}>
                <small>Note: Prepare must be clicked before stepping or running if inputs changed.</small>
              </div>
            </div>
          </div>

          <div style={{ marginTop: 12 }}>
            <h4>Log / Snapshot Details</h4>
            <div style={{ maxHeight: 220, overflow: "auto", border: "1px solid #ddd", padding: 8 }}>
              {displaySnapshots.length === 0 && <div style={{ color: "#666" }}>No steps yet. Click Prepare and then Next/Run.</div>}
              {displaySnapshots.map((s, idx) => (
                <div key={idx} style={{ padding: 6, background: idx === currentStep - 1 ? "#f8f9ff" : "transparent", marginBottom: 6, borderRadius: 6 }}>
                  <div><strong>Step {s.step + 1}</strong>  Requested page: <strong>{s.page}</strong>  {s.fault ? <span style={{ color: "crimson" }}>Page Fault</span> : <span style={{ color: "green" }}>Hit</span>}</div>
                  <div style={{ marginTop: 6 }}>
                    Frames: {s.frames.map((p, i) => (
                      <span key={i} style={{ display: "inline-block", minWidth: 36, textAlign: "center", border: "1px solid #ccc", padding: "4px 6px", marginRight: 6, borderRadius: 4, background: s.evicted === p ? "#ffecec" : "#fff" }}>{p === null ? "-" : p}</span>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      <footer style={{ marginTop: 18, fontSize: 13, color: "#444" }}>
        <strong>Features implemented:</strong> FIFO, LRU, Optimal; step/run; compare; save/load; export. | Built with React only  frontend.
      </footer>
    </div>
  );
}

// --- Subcomponents ---
function SimulationTable({ snapshots, framesCount, currentStep }) {
  // build table rows: each step is a new row, columns = frames
  const maxRows = Math.max(1, snapshots.length);
  return (
    <div style={{ border: "1px solid #ddd", padding: 8, borderRadius: 6 }}>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th style={styles.th}>#</th>
            <th style={styles.th}>Req</th>
            {[...Array(framesCount)].map((_, i) => (
              <th key={i} style={styles.th}>F{i}</th>
            ))}
            <th style={styles.th}>Fault</th>
          </tr>
        </thead>
        <tbody>
          {snapshots.map((s, i) => (
            <tr key={i} style={{ background: i === currentStep - 1 ? "#eef" : "transparent" }}>
              <td style={styles.td}>{i + 1}</td>
              <td style={styles.td}>{s.page}</td>
              {s.frames.map((p, j) => (
                <td key={j} style={styles.td}>{p === null ? "-" : p}</td>
              ))}
              <td style={styles.td}>{s.fault ? "Yes" : "No"}</td>
            </tr>
          ))}
          {snapshots.length === 0 && (
            <tr>
              <td colSpan={3 + framesCount} style={{ padding: 12, color: "#666" }}>No data</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

function SimpleBarChart({ data }) {
  // data = { FIFO: num, LRU: num, Optimal: num }
  const entries = Object.entries(data);
  const max = Math.max(...entries.map(([, v]) => v));
  return (
    <div style={{ padding: 8, border: "1px solid #eee", borderRadius: 6 }}>
      {entries.map(([k, v]) => (
        <div key={k} style={{ marginBottom: 8 }}>
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <div>{k}</div>
            <div><strong>{v}</strong></div>
          </div>
          <div style={{ height: 16, background: "#f1f1f1", borderRadius: 6, marginTop: 6 }}>
            <div style={{ height: "100%", width: `${(v / (max || 1)) * 100}%`, background: "#4c9aff", borderRadius: 6 }} />
          </div>
        </div>
      ))}
    </div>
  );
}

